import axios from "axios";
import React, { useState } from "react";
import "./css/ShippingInfo.css";

interface ShippingInfoProps {
  onShippingInfoChange: (info: string) => void;
}

const ShippingInfo: React.FC<ShippingInfoProps> = ({
  onShippingInfoChange,
}) => {
  const [cep, setCep] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [addressInfo, setAddressInfo] = useState<string>("");

  const handleCepChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const formattedCep = event.target.value.replace(/[^0-9]/g, "");
    setCep(formattedCep);
  };

  const handleSearchClick = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`https://viacep.com.br/ws/${cep}/json/`);
      setAddressInfo(
        `CEP: ${response.data.cep}, ${response.data.localidade}, ${response.data.uf}`
      );
      onShippingInfoChange(addressInfo);
    } catch (error) {
      setError(
        "Erro na consulta. Por favor, verifique o CEP e tente novamente."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="shipping-info-container">
      <label className="shipping-info-label">CEP:</label>
      <input
        className="shipping-info-input"
        type="text"
        value={cep}
        onChange={handleCepChange}
        placeholder="(ex: 12345678)"
        maxLength={8}
      />
      <button className="shipping-info-container" onClick={handleSearchClick} disabled={loading}>
        Consultar
      </button>

      {loading && <p>Carregando...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {addressInfo && <p>{addressInfo}</p>}
    </div>
  );
};

export default ShippingInfo;
